/* Text meant only for screen readers */
.screen-reader-text {
	clip: rect(1px, 1px, 1px, 1px);
	position: absolute !important;
	height: 1px;
	width: 1px;
	overflow: hidden;
}

.screen-reader-text:hover,
.screen-reader-text:active,
.screen-reader-text:focus {
	background-color: #f1f1f1;
	border-radius: 3px;
	box-shadow: 0 0 2px 2px rgba(0, 0, 0, 0.6);
	clip: auto !important;
	color: #21759b;
	display: block;
	font-size: 14px;
	font-weight: bold;
	height: auto;
	left: 5px;
	line-height: normal;
	padding: 15px 23px 14px;
	text-decoration: none;
	top: 5px;
	width: auto;
	z-index: 100000; /* Above WP toolbar */
}



img {
	display: block;
	border: 0;
	width: 100%;
	height: auto;
}


/* Header */

.masthead {
	padding: 0 1em;
	text-align: center;
	border-bottom: 1px solid hsl(0, 0%, 90%);
}


/*--------------------------------------------------------------
Cards:
--------------------------------------------------------------*/

.cards {
	max-width: 50em;
	margin: 1em auto;
	padding: 0;
	list-style-type: none;

}

.cards a {
	display: block;
	margin-bottom: 1em;
	text-decoration: none;
}

.caption {
	padding: 1em;
	font-size: 90%;
	color: black;
}

.caption-title {
	margin: 0;
	font-size: 1.4em;
	line-height: 1em;
}

.caption p {
	margin-top: .5em;
	margin-bottom: 0;
}

.cards a:focus .caption,
.cards a:hover .caption {
	background: hsl(0, 0%, 97%);
}

/*--------------------------------------------------------------
CSS Grid layout for wider screens, when browser supports grid:
--------------------------------------------------------------*/
@supports (grid-area: auto) {

	@media screen and (min-width: 512px) {

		.cards {
			margin: 0 auto;
			padding: 1em;
		}

		.card {
			margin: 1em 0;
			padding: .5em;
			display: grid;
			grid-template-columns: 8em auto;
		}

		.cards a:focus,
		.cards a:hover {
			outline: 1px solid hsl(0, 0%, 80%);
		}
	}

	@media screen and (min-width: 512px) {

		.cards {
			max-width: none;
			margin: 1em 0;
			display: grid;
			grid-template-columns: repeat(2, 25em);
			grid-gap: 1em;
			justify-content: center;
		}

		.card {
			margin: 0;
		}

		.double {
			grid-row: span 2;
		}

		.double .card {
			display: block;
		}

	}

		@media screen and (min-width: 512px) {

		.cards-2 {
			max-width: none;
			margin: 1em 0;
			display: grid;
			grid-template-columns: repeat(3, 1fr);
			grid-gap: 1em;
			justify-content: center;
		}

		.card {
			margin: 0;
		}

		.double {
			grid-row: span 2;
		}

		.double .card {
			display: block;
		}

	}
	

.aw-icon {
	font-size: 75px;
	color: #0000;
	text-shadow: 2px 2px 2px ;
}
	
@supports (grid-area: auto) {

	@media screen and (min-width: 512px) {
	.icon-icon {
	display: grid;
	grid-template-columns: 	repeat(3, 1fr);
			grid-gap: 3em;
			justify-content: center;
}
	
	}
}
	.widget-title {
		text-align: center;
			font-size: 3rem;
	}
	.front-page-2 .widget-title {
		font-size: 3rem;
	}
	.ft-2 ul{
		display: inline;
	}
	.widget_text h3{
		color: black;
	}
	
	.widget_text h4{
		color: black;
	}
	.card-item a{
		display: grid;
		grid-template-columns: (2fr, 2fr);
	}
	.w3-white, .w3-hover-white:hover {
    color: #000!important;
	}
}

	
		@media (min-width: 768px) {
			.wrap {
    max-width: 1280px;
    padding: 60px;
		}
}
